import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './ProductList.css'; // Add specific styles if needed

function ProductList() {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [cart, setCart] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:8000/api/products/')
      .then(response => {
        setProducts(response.data);
        setLoading(false);
      })
      .catch(error => {
        setError('There was an error fetching the products.');
        setLoading(false);
      });
  }, []);

  const handleAddToCart = (product) => {
    setCart([...cart, product]);
    console.log(`Added to cart: ${product.name}`);
  };

  const handleBuyNow = (product) => {
    // Implement buy now logic (e.g., redirect to checkout page)
    console.log(`Buying now: ${product.name}`);
  };

  if (loading) return <p>Loading...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div className="product-list">
      {products.length > 0 ? (
        products.map(product => (
          <div key={product.id} className="product-item">
            <h2>{product.name}</h2>
            <img src={`http://localhost:8000${product.image_url}`} alt={product.name} />
            <p>{product.description}</p>
            <p>מחיר: ₪{product.price}</p> {/* Display price in shekels */}
            <button onClick={() => handleAddToCart(product)}>Add to Cart</button>
            <button onClick={() => handleBuyNow(product)}>Buy Now</button>
          </div>
        ))
      ) : (
        <p>No products available</p>
      )}
    </div>
  );
}

export default ProductList;
