import React from "react";
import "./App.css";
import ProductList from "./ProductList";
import Slideshow from "./Slideshow"; // Import the Slideshow component
import ContactUs from "./ContactUs"; // Import the Contact Us component
import aboutImage from "./images/about-us.jpg"; // Import the image

function App() {
  const handleScrollTo = (id) => {
    document.getElementById(id).scrollIntoView({ behavior: "smooth" });
  };

  return (
    <div className="App">
      <header className="App-header">
        <div className="header-content">
          <div className="navbar">
            <a href="#" onClick={() => handleScrollTo('home')}>转</a>
            <a href="#" onClick={() => handleScrollTo('products')}>爪专</a>
            <a href="#" onClick={() => handleScrollTo('about')}>拽爪转 注</a>
            <a href="#" onClick={() => handleScrollTo('contact')}>爪专 拽砖专</a>
            <a href="#" onClick={() => handleScrollTo('cart')}>注</a>
          </div>
          <h1 className="header-title">砖 </h1>
        </div>
      </header>
      <main>
        <Slideshow /> {/* Add the Slideshow component here */}
        <div id="products" className="ProductList">
          <ProductList /> {/* ProductList Component */}
        </div>
        <div id="about" className="AboutUs">
          <h2>拽爪转 注</h2>
          <img src={aboutImage} alt="About Us" className="about-image" />{" "}
          {/* Image */}
          <p>
            注: 爪 砖 拽, 转 砖 转 专, 砖 注专  
            注, 砖专 驻专 专 砖 专 住驻专 砖 专转 砖. 砖 砖 住祝
            拽驻 驻专 驻专 砖住,  砖 转 注 注砖专
            注 转专. 转拽转 转 砖 砖 砖 注转 砖 砖专转
            , 注 砖 注 砖专 注 转 驻砖 注.  拽拽 砖  砖
            砖拽祝 转 驻  砖 专 专转 砖 转  砖 爪专 砖
            转.  砖驻 拽 住专 砖转   砖 转 转
            注专, 转 拽转 转拽 注转 砖 专转 砖.
          </p>
        </div>
        <div id="contact">
          <ContactUs /> {/* Contact Us Component */}
        </div>
      </main>
    </div>
  );
}

export default App;
