import React from "react";
import {
  BrowserRouter as Router,
  Route,
  Routes,
  Link,
  useNavigate,
} from "react-router-dom";
import { Element, scroller } from "react-scroll";
import "./App.css";
import ProductList from "./components/ProductList";
import ContactUs from "./components/ContactUs";
import aboutImage from "./images/about-us.jpg";
import CartPage from "./pages/CartPage"; // Ensure this path is correct
import { CartProvider, useCart } from "./CartContext";

function App() {
  return (
    <CartProvider>
      <Router>
        <div className="App">
          <Header />
          <main>
            <Routes>
              <Route path="/" element={<Home />} />
              <Route path="/cart" element={<CartPage />} />
            </Routes>
          </main>
        </div>
      </Router>
    </CartProvider>
  );
}

function Header() {
  const navigate = useNavigate();
  const { getCartCount } = useCart();

  const handleScrollToSection = (sectionName) => {
    scroller.scrollTo(sectionName, {
      smooth: true,
      duration: 600,
      offset: -120,
    });
  };

  const handleNavigation = (path, sectionName) => {
    navigate(path, { replace: true });

    setTimeout(() => {
      handleScrollToSection(sectionName);
    }, 200);
  };

  return (
    <header className="App-header">
      <div className="header-content">
        <div className="navbar">
          <Link to="/" onClick={() => handleNavigation("/", "home")}>
            转
          </Link>
          <Link to="/" onClick={() => handleNavigation("/", "products")}>
            爪专
          </Link>
          <Link to="/" onClick={() => handleNavigation("/", "about")}>
            拽爪转 注
          </Link>
          <Link to="/" onClick={() => handleNavigation("/", "contact")}>
            爪专 拽砖专
          </Link>
          <Link to="/cart" className="cart-link">
            注
            <span className="cart-count">{getCartCount()}</span>
          </Link>
        </div>
        <h1 className="header-title">砖 </h1>
      </div>
    </header>
  );
}

function Home() {
  return (
    <>
      <Element name="home">
        <div className="home-section">
          <h1>专  砖 </h1>
          <p>
            转 专 转 转专 砖 爪专 砖 注 专 砖专 注.
          </p>
          <ProductList /> {/* Your product list displayed on the homepage */}
        </div>
      </Element>
      <Element name="about">
        <div className="AboutUs">
          <h2>拽爪转 注</h2>
          <img src={aboutImage} alt="About Us" className="about-image" />
          <p>
            注: 爪 砖 拽, 转 砖 转 专, 砖 注专  
            注, 砖专 驻专 专 砖 专 住驻专 砖 专转 砖. 砖 砖 住祝
            拽驻 驻专 驻专 砖住,  砖 转 注 注砖专
            注 转专. 转拽转 转 砖 砖 砖 注转 砖 砖专转
            , 注 砖 注 砖专 注 转 驻砖 注.  拽拽 砖  砖
            砖拽祝 转 驻  砖 专 专转 砖 转  砖 爪专 砖
            转.  砖驻 拽 住专 砖转   砖 转 转
            注专, 转 拽转 转拽 注转 砖 专转 砖.
          </p>
        </div>
      </Element>
      <Element name="contact">
        <ContactUs />
      </Element>
    </>
  );
}

export default App;
