import React from 'react';
import './css/BuyNowButton.css'; // Correct path to the CSS file

function BuyNowButton({ product, onClick }) {
  return (
    <div className="buy-now-container">
      <button className="buy-now-button" onClick={() => onClick(product)}>קנה עכשיו</button>
    </div>
  );
}

export default BuyNowButton;
