import React from 'react';
import { useCart } from '../CartContext';
import './CartPage.css';

const CartPage = () => {
  const { cartItems, getTotalPrice, removeFromCart } = useCart();

  return (
    <div className="CartPage">
      <h2>העגלה שלך</h2>
      {cartItems.length === 0 ? (
        <p>העגלה שלך ריקה</p>
      ) : (
        <>
          {cartItems.map(item => (
            <div key={item.id} className="cart-item">
              <img src={item.image_url} alt={item.name} className="cart-item-image" />
              <div className="cart-item-details">
                <span className="cart-item-name">{item.name}</span>
                <span className="cart-item-quantity">כמות: {item.quantity}</span>
                <span className="cart-item-price">מחיר: ₪{item.price}</span>
                <button onClick={() => removeFromCart(item.id)}>הסר</button>
              </div>
            </div>
          ))}
          <div className="cart-total">
            סך הכל: ₪{getTotalPrice().toFixed(2)}
          </div>
        </>
      )}
    </div>
  );
};

export default CartPage;
