// src/components/Cart.js
import React, { useContext } from 'react';
import { CartContext } from '../contexts/CartContext';

function Cart() {
  const { cartItems } = useContext(CartContext);

  return (
    <div className="cart-page">
      <h2>עגלה</h2>
      {cartItems.length === 0 ? (
        <p>העגלה שלך ריקה</p>
      ) : (
        <ul>
          {cartItems.map((item, index) => (
            <li key={index}>
              <h3>{item.name}</h3>
              <p>מחיר: ₪{item.price}</p>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default Cart;
