// src/pages/CartPage.js
import React from 'react';
import { useCart } from '../contexts/CartContext'; // Correct path

function CartPage() {
  const { cart } = useCart(); // Use the cart from CartContext

  return (
    <div className="cart-page">
      <h1>עגלה</h1>
      {cart.length > 0 ? (
        <ul>
          {cart.map((item, index) => (
            <li key={index}>
              <h2>{item.name}</h2>
              <p>מחיר: ₪{item.price}</p>
            </li>
          ))}
        </ul>
      ) : (
        <p>העגלה שלך ריקה</p>
      )}
    </div>
  );
}

export default CartPage;
