import React from 'react';
import { useCart } from '../CartContext';
import Button from '@mui/material/Button';
import { styled } from '@mui/material/styles';
import './css/CartPage.css';

// Styled Button for Checkout
const CheckoutButton = styled(Button)({
  backgroundColor: '#f5b61f',
  color: '#fff',
  '&:hover': {
    backgroundColor: '#e4a01f',
  },
  marginTop: '20px',
  padding: '10px 20px',
  borderRadius: '8px',
  width: '100%',
  fontSize: '18px',
  fontWeight: 'bold',
});

function CartPage() {
  const { cartItems } = useCart();

  const handleCheckout = () => {
    // Logic to handle checkout, like navigating to the checkout page
    console.log('Proceeding to checkout...');
  };

  return (
    <div className="cart-page">
      <h2 className="cart-title">העגלה שלך</h2>
      {cartItems.length > 0 ? (
        <>
          {cartItems.map((item, index) => (
            <div key={index} className="cart-item">
              <img src={`/media/product_images/${item.image_url}`} alt={item.name} className="cart-item-image" />
              <div className="cart-item-details">
                <h3>{item.name}</h3>
                <p>{item.description}</p>
                <p>כמות: {item.quantity}</p>
                <p>מחיר: ₪{item.price}</p>
              </div>
            </div>
          ))}
          <CheckoutButton onClick={handleCheckout}>לתשלום</CheckoutButton>
        </>
      ) : (
        <p className="cart-empty">העגלה שלך ריקה</p>
      )}
    </div>
  );
}

export default CartPage;
