import React from 'react';
import { useCart } from '../CartContext';
import { Button, Card, CardContent, CardMedia, Typography, Box } from '@mui/material';
import { styled } from '@mui/material/styles';
import './CartPage.css';

// Styled components
const CartCard = styled(Card)(({ theme }) => ({
  display: 'flex',
  alignItems: 'center',
  marginBottom: theme.spacing(3),
  borderRadius: '16px',
  boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
}));

const CartMedia = styled(CardMedia)({
  width: 150,
  height: 150,
  borderRadius: '16px',
});

const CartDetails = styled(CardContent)(({ theme }) => ({
  flex: 1,
  paddingLeft: theme.spacing(2),
  paddingRight: theme.spacing(2),
}));

const CheckoutButton = styled(Button)({
  backgroundColor: '#f5b61f',
  color: '#fff',
  borderRadius: '16px',
  padding: '10px 20px',
  fontSize: '18px',
  fontWeight: 'bold',
  marginTop: '20px',
  '&:hover': {
    backgroundColor: '#e4a01f',
  },
});

const CartPage = () => {
  const { cartItems, removeFromCart, getTotalPrice } = useCart();

  return (
    <Box className="cart-page">
      <Typography variant="h4" component="h1" align="center" gutterBottom>
        עגלה
      </Typography>
      {cartItems.length === 0 ? (
        <Typography variant="h6" align="center">
          העגלה ריקה
        </Typography>
      ) : (
        <Box className="cart-items">
          {cartItems.map((item) => (
            <CartCard key={item.id}>
              <CartMedia
                component="img"
                image={`http://localhost:8000${item.image_url}`}
                alt={item.name}
              />
              <CartDetails>
                <Typography variant="h6">{item.name}</Typography>
                <Typography>מחיר: ₪{item.price}</Typography>
                <Typography>כמות: {item.quantity}</Typography>
                <Button
                  variant="contained"
                  color="secondary"
                  onClick={() => removeFromCart(item.id)}
                  sx={{ marginTop: '10px' }}
                >
                  הסר
                </Button>
              </CartDetails>
            </CartCard>
          ))}
          <Typography variant="h5" align="center" sx={{ marginTop: '20px' }}>
            סך הכל: ₪{getTotalPrice()}
          </Typography>
          <CheckoutButton fullWidth>לתשלום</CheckoutButton>
        </Box>
      )}
    </Box>
  );
};

export default CartPage;
