import React from 'react';
import { useCart } from '../CartContext';
import './CartPage.css'; // Ensure you have a corresponding CSS file

function CartPage() {
  const { cartItems, getTotalPrice } = useCart();

  if (!cartItems || cartItems.length === 0) {
    return <p>Your cart is empty.</p>;
  }

  return (
    <div className="cart-page">
      <h2>עגלה</h2>
      <div className="cart-items">
        {cartItems.map(item => {
          const price = Number(item.price); // Ensure price is a number
          const totalPrice = (price * item.quantity).toFixed(2);

          return (
            <div key={item.id} className="cart-item">
              <img src={`http://localhost:8000${item.image_url}`} alt={item.name} className="cart-item-image" />
              <div className="cart-item-details">
                <h3>{item.name}</h3>
                <p>Quantity: {item.quantity}</p>
                <p>Price: ₪{price.toFixed(2)}</p>
                <p>Total: ₪{totalPrice}</p>
              </div>
            </div>
          );
        })}
      </div>
      <div className="cart-total">
        <h3>Total Price: ₪{getTotalPrice().toFixed(2)}</h3>
      </div>
    </div>
  );
}

export default CartPage;
