// src/pages/CartPage.js

import React from 'react';
import { useCart } from '../CartContext';
import './CartPage.css';

const CartPage = () => {
  const { cartItems, getTotalPrice } = useCart();

  return (
    <div className="cart-page">
      <h2 className="cart-title">עגלה</h2>
      {cartItems.length === 0 ? (
        <p className="empty-cart">העגלה ריקה</p>
      ) : (
        <div className="cart-items">
          {cartItems.map(item => (
            <div key={item.id} className="cart-item">
              <img 
                src={`/media/product_images/${item.image_url}`} 
                alt={item.name} 
                className="cart-item-image" 
              />
              <div className="cart-item-details">
                <h3 className="cart-item-name">{item.name}</h3>
                <p className="cart-item-description">{item.description}</p>
                <p className="cart-item-price">₪{item.price}</p>
                <p className="cart-item-quantity">כמות: {item.quantity}</p>
              </div>
            </div>
          ))}
          <div className="cart-summary">
            <h3 className="total-price">סך הכל: ₪{getTotalPrice().toFixed(2)}</h3>
          </div>
        </div>
      )}
    </div>
  );
};

export default CartPage;
