// src/components/CartPage.js
import React, { useContext } from 'react';
import { CartContext } from '../contexts/CartContext';
import './css/CartPage.css'; // Ensure to create this CSS file for cart page styling

function CartPage() {
  const { cartItems, removeFromCart } = useContext(CartContext);

  const handleRemove = (productId) => {
    removeFromCart(productId);
  };

  return (
    <div className="cart-page">
      <h2>עגלה</h2>
      {cartItems.length > 0 ? (
        <ul>
          {cartItems.map(item => (
            <li key={item.id}>
              <h3>{item.name}</h3>
              <p>מחיר: ₪{item.price}</p>
              <button onClick={() => handleRemove(item.id)}>הסר מהעגלה</button>
            </li>
          ))}
        </ul>
      ) : (
        <p>העגלה ריקה</p>
      )}
    </div>
  );
}

export default CartPage;
