// src/pages/CartPage.js
import React from "react";
import { useCart } from "../CartContext";
import Button from "@mui/material/Button";
import "./CartPage.css";

const CartPage = () => {
  const { cartItems, removeFromCart, getTotalPrice } = useCart();

  const handleCheckout = async () => {
    try {
      // Construct the order data from cart items
      const orderData = {
        customer_name: "John Doe", // Replace with actual customer data
        customer_email: "john.doe@example.com",
        total_amount: getTotalPrice(),
        products: cartItems.map(item => ({
          product: item.id,
          quantity: item.quantity
        }))
      };

      // Send the order data to the backend
      const response = await fetch("http://localhost:8000/api/checkout/", {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify(orderData)
      });

      if (response.ok) {
        const result = await response.json();
        console.log("Order created:", result);
        // Handle successful checkout (e.g., navigate to a confirmation page)
        alert("Order placed successfully!");
        // Clear cart items after successful order
        // Optionally redirect to a confirmation page
        // window.location.href = "/confirmation"; // Update with actual confirmation route
      } else {
        console.error("Error creating order:", response.statusText);
        alert("Failed to place order. Please try again.");
      }
    } catch (error) {
      console.error("Network error:", error);
      alert("Network error occurred. Please try again.");
    }
  };

  return (
    <div className="cart-page">
      <h1>עגלה</h1>
      {cartItems.length === 0 ? (
        <p>העגלה ריקה</p>
      ) : (
        <div className="cart-items">
          {cartItems.map(item => (
            <div key={item.id} className="cart-item">
              <img
                src={`http://localhost:8000${item.image_url}`}
                alt={item.name}
                className="cart-item-image"
              />
              <div className="cart-item-details">
                <h2>{item.name}</h2>
                <p>מחיר: ₪{item.price}</p>
                <p>כמות: {item.quantity}</p>
                <button onClick={() => removeFromCart(item.id)}>הסר</button>
              </div>
            </div>
          ))}
          <div className="cart-total">
            <h2>סך הכל: ₪{getTotalPrice()}</h2>
          </div>
          <Button
            className="checkout-button"
            variant="contained"
            color="primary"
            onClick={handleCheckout}
            style={{ backgroundColor: "#f5b61f", color: "#fff" }}
          >
            לתשלום
          </Button>
        </div>
      )}
    </div>
  );
};

export default CartPage;
