// src/App.js
import React, { useContext } from 'react';
import { Link, Element } from 'react-scroll';
import './App.css';
import ProductList from './components/ProductList';
import Slideshow from './Slideshow'; 
import ContactUs from './components/ContactUs';
import aboutImage from './images/about-us.jpg';
import { CartContext } from './contexts/CartContext';

function App() {
  const { cartItems } = useContext(CartContext);

  return (
    <div className="App">
      <header className="App-header">
        <div className="header-content">
          <div className="navbar">
            <Link to="home" smooth={true} duration={500}>בית🐝</Link>
            <Link to="products" smooth={true} duration={500}>מוצרים🍯</Link>
            <Link to="about" smooth={true} duration={500}>קצת עלינו🌸</Link>
            <Link to="contact" smooth={true} duration={500}>צרו קשר🧇</Link>
            <Link to="cart" smooth={true} duration={500}>
              עגלה🛒<span className="cart-count">{cartItems.length}</span>
            </Link>
          </div>
          <h1 className="header-title">דבש האילן</h1>
        </div>
      </header>
      <main>
        <Slideshow />
        <Element name="products">
          <ProductList />
        </Element>
        <Element name="about">
          <div className="AboutUs">
            <h2>קצת עלינו</h2>
            <img src={aboutImage} alt="About Us" className="about-image" />
            <p>
              עלינו: אצלנו במושב אליקים, תהנו מדבש איכותי וטהור...
            </p>
          </div>
        </Element>
        <Element name="contact">
          <ContactUs />
        </Element>
        <Element name="cart">
          {/* Cart Page Component (Step 5) */}
        </Element>
      </main>
    </div>
  );
}

export default App;
