import React, { useContext } from "react";
import { BrowserRouter as Router, Route, Routes, Link, useNavigate } from "react-router-dom";
import "./App.css";
import ProductList from "./components/ProductList";
import Slideshow from "./Slideshow";
import ContactUs from "./components/ContactUs";
import aboutImage from "./images/about-us.jpg";
import { CartContext } from "./contexts/CartContext";
import CartPage from "./components/CartPage";

function App() {
  const { cartItems } = useContext(CartContext);
  const navigate = useNavigate();

  return (
    <Router>
      <div className="App">
        <header className="App-header">
          <div className="header-content">
            <div className="navbar">
              <Link to="/">בית🐝</Link>
              <Link to="#products">מוצרים🍯</Link>
              <Link to="#about">קצת עלינו🌸</Link>
              <Link to="#contact">צרו קשר🧇</Link>
              <Link to="/cart">
                עגלה🛒<span className="cart-count">{cartItems.length}</span>
              </Link>
            </div>
            <h1 className="header-title">דבש האילן</h1>
          </div>
        </header>
        <main>
          <Routes>
            <Route path="/" element={
              <>
                <Slideshow />
                <div id="products">
                  <ProductList />
                </div>
                <div id="about" className="AboutUs">
                  <h2>קצת עלינו</h2>
                  <img src={aboutImage} alt="About Us" className="about-image" />
                  <p>עלינו: אצלנו במושב אליקים, תהנו מדבש איכותי וטהור...</p>
                </div>
                <div id="contact">
                  <ContactUs />
                </div>
              </>
            } />
            <Route path="/cart" element={<CartPage />} />
          </Routes>
        </main>
      </div>
    </Router>
  );
}

export default App;
