import React from 'react';
import './App.css';
import ProductList from './ProductList';
import Slideshow from './Slideshow'; // Import the Slideshow component
import aboutImage from './images/about-us.jpg'; // Import the image

function App() {
  return (
    <div className="App">
      <header className="App-header">
        <div className="header-content">
          <div className="navbar">
            <a href="#home">转</a>
            <a href="#products">爪专</a>
            <a href="#about">拽爪转 注</a>
            <a href="#contact">爪专 拽砖专锔</a>
          </div>
          <h1 className="header-title">砖 </h1>
        </div>
      </header>
      <main>
        <Slideshow /> {/* Add the Slideshow component here */}
        <div id="products" className="ProductList">
          <ProductList /> {/* ProductList Component */}
        </div>
        <div id="about" className="AboutUs"> {/* About Us section */}
          <h2>About Us</h2>
          <img src={aboutImage} alt="About Us" className="about-image" /> {/* Image */}
          <p>
            Welcome to 砖 , where we provide the finest honey straight from the hive. 
            Our mission is to offer pure, natural, and delicious honey products that bring the taste of nature to your home. 
            With a commitment to sustainability and quality, we ensure that every jar of honey is harvested with care and respect for the environment.
          </p>
        </div>
      </main>
    </div>
  );
}

export default App;
