import React from 'react';
import { useCart } from '../CartContext'; // Ensure this path is correct
import './CartPage.css'; // Ensure this path is correct

function CartPage() {
  const { cartItems, removeFromCart, getTotalPrice } = useCart();

  const handleRemove = (id) => {
    removeFromCart(id);
  };

  return (
    <div className="cart-page">
      <h1>עגלה שלך</h1>
      {cartItems.length > 0 ? (
        <>
          {cartItems.map(item => (
            <div key={item.id} className="cart-item">
              <img src={`http://localhost:8000${item.image_url}`} alt={item.name} />
              <div className="cart-item-details">
                <h2>{item.name}</h2>
                <p className="quantity">Quantity: {item.quantity}</p>
                <p className="price">Price: ₪{item.price.toFixed(2)}</p>
              </div>
              <div className="cart-item-remove" onClick={() => handleRemove(item.id)}>
                Remove
              </div>
            </div>
          ))}
          <div className="cart-total">
            <h2>Total: ₪{getTotalPrice().toFixed(2)}</h2>
          </div>
        </>
      ) : (
        <p>Your cart is empty.</p>
      )}
    </div>
  );
}

export default CartPage;
