import React from 'react';
import { useCart } from '../CartContext';

const CartPage = () => {
  const { cartItems, getTotalPrice, removeFromCart } = useCart();

  return (
    <div className="CartPage">
      <h2>העגלה שלי</h2>
      {cartItems.length === 0 ? (
        <p>העגלה שלך ריקה</p>
      ) : (
        <div>
          <ul>
            {cartItems.map(item => (
              <li key={item.id}>
                <span>{item.name} (x{item.quantity})</span>
                <button onClick={() => removeFromCart(item.id)}>הסר</button>
              </li>
            ))}
          </ul>
          <h3>סה"כ: {getTotalPrice().toFixed(2)} ₪</h3>
        </div>
      )}
    </div>
  );
};

export default CartPage;
