// src/components/BuyNowButton.js

import React from 'react';
import './css/BuyNowButton.css'; // Ensure the correct path to CSS file

function BuyNowButton({ product, onClick }) {
  return (
    <button className="buy-now-button" onClick={() => onClick(product.id)}>
      קנה עכשיו
    </button>
  );
}

export default BuyNowButton;
